mapper.select:
select m_id
from salad_custom
where m_id = #{m_id}

select rcp_cmbnt
from salad_custom
where m_id = #{m_id}

select rcp_cmbnt
from salad_custom
where m_id = #{m_id}

mapper.update:
update salad_custom
set rcp_cmbnt = #{rcp_cmbnt}
where m_id = #{m_id}

update salad_custom
set rcp_cmbnt = concat(concat(rcp_cmbnt,","),#{rcp_cmbnt})
where m_id = #{m_id}

mapper.select:
select cart_num
from tbl_cart
where rcp_num=#{rcp_num}
